mapper.select:
SELECT chrom , txStart , txEnd FROM GENE_Refseq_hg19 WHERE name=#{name}
UNION ALL
SELECT chrom , txStart , txEnd FROM GENE_Refseq_hg19 WHERE name2=#{name}
UNION ALL
SELECT chrom , txStart , txEnd FROM GENE_UCSC_hg19 WHERE name=#{name}
UNION ALL
SELECT chrom , txStart , txEnd FROM GENE_Ensembl_hg19 WHERE name=#{name}
UNION ALL
SELECT chrom , txStart , txEnd FROM GENE_Ensembl_hg19 WHERE name2=#{name}
